import React from "react";
import { Link } from "react-router";
import Hauptseite from "../pages/Hauptseite";
import Hus1 from "../pages/Hus1";
import Hus2 from "../pages/Hus2";
import Infos from "../pages/Infos";
import Booking from "../pages/Booking";

import { RxHamburgerMenu } from "react-icons/rx";
import { useState } from "react";

  const MobileNavbar = () => {
    const [click, setClick] = useState(false);
    const toggle = () => {setClick(!click)};
    const my_hide = () => {setClick(false)};

    const Hamburger =
      <RxHamburgerMenu className="HamburgerMenu"
      size="30px" color="black"
      />


    return (
      <nav className="nav-mobile">
        <Link to ="/" element = {<Hauptseite/>} className="nav-item"><h1 className="navlogo">Sonnenhus</h1></Link>
        <button onClick={toggle} className="mobile-menu">
          {Hamburger}
          {click &&
              <div className="mobile-links">
                <Link to ="/" element = {<Hauptseite/>}className="nav-item" onCLick= {toggle} onBlur={my_hide}>Ãœbersicht</Link>
                <Link to ="/hus1" element = {<Hus1/>} className="nav-item" onCLick= {toggle} onBlur={my_hide}>Hus 1</Link>
                <Link to ="/hus2" element = {<Hus2/>} className="nav-item" onCLick= {toggle} onBlur={my_hide}>Hus 2</Link>
                <Link to ="/infos" element = {<Infos/>} className="nav-item" onCLick= {toggle} onBlur={my_hide}>Informationen</Link>
                <Link to = "/buchung" element = {<Booking/>} className="nav-item" onCLick= {toggle} onBlur={my_hide}>Buchungsanfrage</Link>
              </div>
        }
        </button>          
      </nav>
        );
      }


      export default MobileNavbar;
