import React from "react";
import { Link } from "react-router";
import Hauptseite from "../pages/Hauptseite";
import Hus1 from "../pages/Hus1";
import Hus2 from "../pages/Hus2";
import Infos from "../pages/Infos";
import Booking from "../pages/Booking";
import { RxHamburgerMenu } from "react-icons/rx";
import { useState } from "react";
import NavLinks from "./NavLinks"
import ClickAwayListener from "react-click-away-listener";





const MobileNavbar = () => {

  const [click, setClick] = useState(false);
  const [open, setOpen] = useState(false);

  const handleClick = () => {setOpen(!open)};



  const handleClickAway = () => {setOpen(false)};


  const Hamburger = <RxHamburgerMenu className="HamburgerMenu"
  size="30px" color="black"
  onClick={() => setClick(!click)}
  />


  const closeMenu = () => {
    return setClick(false)
  };


  return (
      <nav className="nav-mobile">
       <Link to ="/" element = {<Hauptseite/>} className="nav-item"><h1 className="navlogo">Sonnenhus</h1></Link>

       <div className="mobile-menu">



              {Hamburger}
              <ClickAwayListener onClickAway={(event) => {
        setOpen(false);
       }}>
        <>
              {click && <NavLinks isClicked={true} closeMenu={closeMenu}/>}
        </>
        </ClickAwayListener>   
        </div>          
      </nav>
  );
}


export default MobileNavbar;
