import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./slideshow.css";

const Slideshow = ({images}) => { 
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        prevArrow: <div className="slick-prev">Previous</div>,
        nextArrow: <div className="slick-arrow-next"> <svg xmlns="http://www.w3.org/2000/svg" stroke="black" height="24" viewBox="0 -960 960 960" width="24"><path d="m242-200 200-280-200-280h98l200 280-200 280h-98Zm238 0 200-280-200-280h98l200 280-200 280h-98Z"/></svg>
    Next</div>,
        pauseOnHover: false,
        cssEase: "linear",
    };
    return (
        <div className="slideshow">
        <Slider {...settings}>
            {images.map((image, index) => (
                <div key={index}>
                    <img src={images[index].imagePath} alt="Slideshow" className="slideshow_image" />
                </div>
            ))}
        </Slider>
        </div>
    );
}
export default Slideshow;
