import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import { useState, useEffect } from "react";

const SliderThumb = ({images}) => { 
    const settings = {
      dots: true,
      speed: 500,
      slidesToShow: 2,
      slidesToScroll: 2,
      infinite: true,
      autoplay: true,
      autoplaySpeed: 1000,
      onReInit: () => setCurrentSlide(slider1?.innerSlider.state.currentSlide),
    lazyLoad: true,
    asNavFor: ".slider-nav",
    focusOnSelect: true,
    };


    return ( 
        <div className="slideshow">
        <Slider {...settings}
            asNavFor={nav1}
            ref={(slider) => setSlider1(slider)}
        >
        </Slider>
        <div className="thumb-wrapper">
          {images.map((item, idx) => (
            <div
              key={item.id}
              className={currentSlide === idx ? "active": null}
              onClick={() => {
                slider1?.slickGoTo(idx)
              }}>
              <img src={item.imagePath} alt="Slideshow" className="thumbnail-image" />
              {currentSlide}
            </div>
          ))}
        </div>

        <Slider {...settings}>
            {images.map((image, index) => (
                <div key={index}>
                    <img src={images[index].imagePath} alt="Slideshow" className="slideshow_image" />
                </div>
            ))}
        </Slider>
        </div>
    );
